# example
package main

import "fmt"

var (
	di = []int{0, 1, 2}
	dj = []int{0, 1}
	dk = []int{0, 1, 2, 3, 4}
)

func main() {
	getResult(di, dj, dk)
}

func getResult(di, dj, dk []int) {
	for _, i := range di {
		for _, j := range dj {
			for _, k := range dk {
				fmt.Printf("[%d, %d, %d] \t", i, j, k)
			}
		}
	}
}
```

