        var timestamp = time % 1 === 0 ? parseInt(time) : new Date(time).getTime(); // è·å–æ—¶é—´æˆ³
        var timestamp = String(timestamp).length === 10 ? timestamp : Math.round(timestamp / 1000); // å»æ‰æ¯«ç§’
        var mistiming = Math.round(new Date().getTime() / 1000) - timestamp;
        var postfix = mistiming > 0 ? 'å‰' : 'å'
        var arrr = ['å¹´', 'ä¸ªæœˆ', 'æ˜ŸæœŸ', 'å¤©', 'å°æ—¶', 'åˆ†é’Ÿ', 'ç§’'];
        var arrn = [31536000, 2592000, 604800, 86400, 3600, 60, 1];
        mistiming = Math.abs(mistiming);
