      <form
        class="pushdeer-form"
        method="get"
        action="https://api2.pushdeer.com/message/push"
        id="the-form"
      >
        <input
          type="hidden"
          name="pushkey"
          id="pushdeer-key"
          value="PDU6998TyRPY1S0snjIfxS52HZXyXSznhEwOwSis"
          required
