```go
package main

type Optional[T any] struct {
	Some   T
	IsNull bool
}

func Some[T any](value T) Optional[T] {
	return Optional[T]{
		value,
		false,
	}
}

type Result[T any] struct {
	OK    T
	Error error
}

func OK[T any](value T) Result[T] {
	return Result[T]{value, nil}
}

func a() Result[string] {
	return OK("1")
}

func main() {
	var s = Some(1)
	if s.IsNull {

	}
	var b = OK(a())
	if b.Error != nil {

	}
}

```

