public class ByzantineGenerals {
â€‹
 Â  Â private static final int NUM_GENERALS = 6;
â€‹
 Â  Â private static final int REPEAT = 5;
 Â  Â static int traitor;
 Â  Â static int traitor2;
 Â  Â public static void main(String[] args) {
â€‹
 Â  Â  Â  Â String[] orders = new String[NUM_GENERALS]; // å‘½ä»¤é›†åˆ
 Â  Â  Â  Â for (int p = 0; p < REPEAT; p++) {
 Â  Â  Â  Â  Â  Â traitor = new Random().nextInt(NUM_GENERALS - 1);
 Â  Â  Â  Â  Â  Â traitor2 = new Random().nextInt(NUM_GENERALS);
 Â  Â  Â  Â  Â  Â if (traitor == traitor2) traitor2 += 1;
 Â  Â  Â  Â  Â  Â for (int i = 0; i < NUM_GENERALS; i++) {
 Â  Â  Â  Â  Â  Â  Â  Â orders[i] = (i == traitor || i == traitor2) ? "retreat" : "attack";
 Â  Â  Â  Â  Â   }
â€‹
 Â  Â  Â  Â  Â  Â System.out.println("orders" + Arrays.toString(orders));
 Â  Â  Â  Â  Â  Â boolean finalDecision = computeFinalDecision(orders);
 Â  Â  Â  Â  Â  Â System.out.println("Final decision: " + (finalDecision ? "attack" : "retreat"));
 Â  Â  Â  Â  Â  Â System.out.println();
 Â  Â  Â   }
 Â   }
â€‹
 Â  Â private static boolean computeFinalDecision(String[] orders) {
 Â  Â  Â  Â boolean[] decisions = new boolean[NUM_GENERALS];
 Â  Â  Â  Â for (int i = 0; i < NUM_GENERALS; i++) {
 Â  Â  Â  Â  Â  Â if (i == traitor || i == traitor2) {
 Â  Â  Â  Â  Â  Â  Â  Â decisions[i] = (new Random().nextBoolean());
 Â  Â  Â  Â  Â   } else {
 Â  Â  Â  Â  Â  Â  Â  Â boolean[] receivedOrders = new boolean[NUM_GENERALS - 1];
 Â  Â  Â  Â  Â  Â  Â  Â int index = 0;
 Â  Â  Â  Â  Â  Â  Â  Â for (int j = 0; j < NUM_GENERALS; j++) {
 Â  Â  Â  Â  Â  Â  Â  Â  Â  Â if (j != i) {
 Â  Â  Â  Â  Â  Â  Â  Â  Â  Â  Â  Â receivedOrders[index++] = (orders[j].equals("attack")); // æ¯ä¸€ä½å°†å†›æ”¶é›†å‘½ä»¤
 Â  Â  Â  Â  Â  Â  Â  Â  Â   }
 Â  Â  Â  Â  Â  Â  Â   }
 Â  Â  Â  Â  Â  Â  Â  Â decisions[i] = computeDecision(receivedOrders);
 Â  Â  Â  Â  Â   }
 Â  Â  Â   }
 Â  Â  Â  Â return computeDecision(decisions);
 Â   }
â€‹
 Â  Â private static boolean computeDecision(boolean[] decisions) {
 Â  Â  Â  Â // Compute the majority decision
 Â  Â  Â  Â int numTrue = 0;
 Â  Â  Â  Â int numFalse = 0;
 Â  Â  Â  Â for (boolean decision : decisions) {
 Â  Â  Â  Â  Â  Â if (decision) {
 Â  Â  Â  Â  Â  Â  Â  Â numTrue++;
 Â  Â  Â  Â  Â   } else {
 Â  Â  Â  Â  Â  Â  Â  Â numFalse++;
 Â  Â  Â  Â  Â   }
 Â  Â  Â   }
 Â  Â  Â  Â return (numTrue > numFalse);
 Â   }
